CREATE EXTENSION IF NOT EXISTS "uuid-ossp"; CREATE TABLE users (id UUID PRIMARY KEY DEFAULT uuid_generate_v4(), email TEXT UNIQUE NOT NULL, created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL, beta BOOLEAN DEFAULT false, subscription_status TEXT DEFAULT 'inactive'); ALTER TABLE users ENABLE ROW LEVEL SECURITY; CREATE POLICY "Users can view own data" ON users FOR SELECT USING (auth.uid() = id); CREATE TABLE projects (id UUID PRIMARY KEY DEFAULT uuid_generate_v4(), user_id UUID REFERENCES users(id) ON DELETE CASCADE, name TEXT NOT NULL, created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL); ALTER TABLE projects ENABLE ROW LEVEL SECURITY; CREATE POLICY "Users can manage own projects" ON projects FOR ALL USING (auth.uid() = user_id); CREATE INDEX idx_users_email ON users(email); CREATE INDEX idx_projects_user_id ON projects(user_id);
